import schema from "api/schema";

export const Result = schema({
    type: "object",
    properties: {
        "errorCode": {
            type: "string"
        },
        "message": {
            type: "string",
            default: ""
        }
    },
    additionalProperties: false,
    required: ["errorCode", "message"]
});

export const WhoAmI = schema({
    type: "object",
    properties: {
        "registeredName": {
            type: "string"
        },
        "registeredNameGeneration": {
            type: "integer"
        }
    },
    additionalProperties: false,
});

export const ProfileInfo = schema({
    type: "object",
    properties: {
        "fullName": {
            type: "string"
        },
        "gender": {
            type: "string"
        },
        "email": {
            type: "string",
            anyOf: [{
                const: ""
            }, {
                format: "email"
            }]
        },
        "operations": {
            type: "object",
            properties: {
                "edit": {
                    type: "array",
                    items: {
                        type: "string"
                    }
                }
            },
            additionalProperties: false,
        }
    },
    additionalProperties: false,
});

export const TokenCreated = schema({
    type: "object",
    properties: {
        "token": {
            type: "string"
        },
        "permissions": {
            type: "array",
            items: {
                type: "string"
            },
            default: []
        }
    },
    additionalProperties: false,
    required: ["token", "permissions"]
});

export const RegisteredNameInfo = schema({
    type: "object",
    properties: {
        "name": {
            type: "string"
        },
        "generation": {
            type: "integer"
        },
        "operationStatus": {
            type: "string"
        },
        "operationStatusUpdated": {
            type: "integer"
        },
        "operationErrorCode": {
            type: "string"
        },
        "operationErrorMessage": {
            type: "string"
        },
        "operations": {
            type: "object",
            properties: {
                "manage": {
                    type: "array",
                    items: {
                        type: "string"
                    }
                }
            },
            additionalProperties: false,
        }
    },
    additionalProperties: false,
});

export const RegisteredNameSecret = schema({
    type: "object",
    properties: {
        "mnemonic": {
            type: "array",
            items: {
                type: "string"
            },
            minItems: 24,
            maxItems: 24
        },
        "secret": {
            type: "string"
        }
    },
    additionalProperties: false,
});
