export class RegisteredName {

    static parse(qName) {
        if (qName == null || qName === "") {
            return new DelegatedName(null, null);
        }
        const parts = qName.split("_");
        return new DelegatedName(parts[0],parts.length > 1 ? parseInt(parts[1]) : null)
    }

}

export class DelegatedName {

    constructor(name, generation) {
        this.name = name;
        this.generation = generation;
    }

    format() {
        return `${this.name}_${this.generation}`;
    }

}
