export const DELEGATED = "delegated";

export function parse(registeredName) {
    if (registeredName == null || registeredName === "") {
        return {
            type: DELEGATED,
            name: null,
            generation: null
        };
    }
    const parts = registeredName.split("_");
    return {
        type: DELEGATED,
        name: parts[0],
        generation: parts.length > 1 ? parseInt(parts[1]) : null
    }
}

export function format(registeredName) {
    return `${registeredName.name}_${registeredName.generation}`;
}
