import React from 'react';

export class Button extends React.PureComponent {

    componentDidMount() {
        if (this.props.autoFocus && this.domNode) {
            this.domNode.focus();
        }
    }

    render() {
        const {variant, ...props} = this.props;
        return (
            <button type="button" className={`btn btn-${variant}`} {...props} ref={(dom) => {this.domNode = dom}}>
                {props.children}
            </button>
        );
    }

}
