import React from 'react';
import cx from 'classnames';

export class Button extends React.PureComponent {

    componentDidMount() {
        if (this.props.autoFocus && this.domNode) {
            this.domNode.focus();
        }
    }

    render() {
        const {variant, size, ...props} = this.props;
        const klass = cx([
            "btn",
            `btn-${variant}`, {
                "btn-sm": size === "sm",
                "btn-lg": size === "lg"
            }
        ]);
        return (
            <button type="button" className={klass} {...props} ref={(dom) => {this.domNode = dom}}>
                {props.children}
            </button>
        );
    }

}
