import React from 'react';
import { Alert } from 'react-bootstrap';
import { connect } from 'react-redux';

import "error/ErrorPane.css";

const ErrorPane = ({message, visible}) => {
    if (visible) {
        return (<Alert variant="danger" className="error-pane">{message}</Alert>);
    } else {
        return null;
    }
};

export default connect(
    (state) => state.error
)(ErrorPane);
