import { ERROR_DISMISS, ERROR_SHOW } from "error/actions";

const initialState = {
    message: "",
    visible: false
};

export default (state = initialState, action) => {
    switch (action.type) {
        case ERROR_SHOW:
            return {
                message: action.payload.message,
                visible: true
            };
        case ERROR_DISMISS:
            return {
                ...state,
                visible: false
            };
        default:
            return state;
    }
}
