export const CONNECT_TO_HOME = "CONNECT_TO_HOME";
export const connectToHome = (location, login, password) => ({
    type: CONNECT_TO_HOME,
    payload: {location, login, password}
});

export const CONNECTION_TO_HOME_FAILED = "CONNECTION_TO_HOME_FAILED";
export const connectionToHomeFailed = (message) => ({
    type: CONNECTION_TO_HOME_FAILED,
    payload: {message}
});

export const CONNECTION_TO_HOME_LOGIN_FAILED = "CONNECTION_TO_HOME_LOGIN_FAILED";
export const connectionToHomeLoginFailed = () => ({
    type: CONNECTION_TO_HOME_LOGIN_FAILED
});


export const CONNECTED_TO_HOME = "CONNECTED_TO_HOME";
export const connectedToHome = (location, login, token) => ({
    type: CONNECTED_TO_HOME,
    payload: {location, login, token}
});
