import React from 'react';
import { connect } from 'react-redux';
import { Field, Formik } from 'formik';
import { Button, Form, Modal } from 'react-bootstrap';

import { cancelConnectDialog } from "logobar/connectionstatus/connectdialog/actions";

const ConnectDialog = ({show, location, login, password, cancelConnectDialog}) => (
    <Formik initialValues={{location, login}}>
        <Modal centered show={show} onHide={cancelConnectDialog}>
            <Modal.Header closeButton>
                <Modal.Title>Connect to home</Modal.Title>
            </Modal.Header>
            <Modal.Body>
                <Form>
                    <Form.Group>
                        <Form.Label>Node URL</Form.Label>
                        <Field component={Form.Control} type="text" name="location" />
                    </Form.Group>
                    <Form.Group>
                        <Form.Label>Login</Form.Label>
                        <Field component={Form.Control} type="text" name="login" />
                    </Form.Group>
                    <Form.Group>
                        <Form.Label>Password</Form.Label>
                        <Field component={Form.Control} type="password" name="password" />
                    </Form.Group>
                </Form>
            </Modal.Body>
            <Modal.Footer>
                <Button variant="secondary" onClick={cancelConnectDialog}>
                    Cancel
                </Button>
                <Button variant="primary">
                    Connect
                </Button>
            </Modal.Footer>
        </Modal>
    </Formik>
);

export default connect(
    (state) => state.connectDialog,
    { cancelConnectDialog }
)(ConnectDialog);
