import { CANCEL_CONNECT_DIALOG, OPEN_CONNECT_DIALOG } from "logobar/connectionstatus/connectdialog/actions";

const initialState = {
    show: false,
    location: "",
    login: "admin",
    password: ""
};

export default (state = initialState, action) => {
    switch (action.type) {
        case OPEN_CONNECT_DIALOG:
            return {
                ...state,
                password: "",
                show: true
            };

        case CANCEL_CONNECT_DIALOG:
            return {
                ...state,
                show: false
            };

        default:
            return state;
    }
}
