import { OWNER_SET, OWNER_VERIFIED } from "mainmenu/owner/actions";

const initialState = {
    name: null,
    generation: 0,
    latest: false,
    verified: false,
    correct: false
};

export default (state = initialState, action) => {
    switch (action.type) {
        case OWNER_SET:
            return {
                ...state,
                ...action.payload,
                verified: false
            };
        case OWNER_VERIFIED:
            if (state.name === action.payload.name) {
                return {
                    ...state,
                    ...action.payload,
                    verified: true
                };
            }
            return state;
        default:
            return state;
    }
}
