import schema from 'schm';

export const Result = schema({
    jsonrpc: String,
    result: {
        type: Object,
        required: true
    },
    id: Number
});

export const ErrorResult = schema({
    jsonrpc: String,
    error: {
        code: {
            type: Number,
            required: true
        },
        message: {
            type: String,
            required: true
        }
    },
    id: Number
});

export const RegisteredNameInfo = schema({
    name: String,
    generation: Number,
    latest: Boolean,
    updatingKey: String,
    nodeUri: String,
    deadline: Number,
    signingKey: String,
    validFrom: Number
});
