import { select, call, apply } from 'redux-saga/effects';

import { formatSchemaErrors, NamingError } from "error";
import * as NamingApi from "naming/api";
import schm from "schm";

let callId = 1;

function* callNaming({method, params, schema = null, notNull = false}) {
    const location = yield select(state => state.naming.location);
    let response;
    try {
        response = yield call(fetch, location, {
            method: "POST",
            headers: {
                "Accept": "application/json",
                "Content-Type": "application/json"
            },
            body: JSON.stringify({
                jsonrpc: "2.0",
                id: callId++,
                method,
                params
            })
        });
    } catch (e) {
        throw new NamingError(e);
    }
    if (!response.ok) {
        throw new NamingError("Server returned error status");
    }
    let data;
    try {
        data = yield apply(response, response.json);
    } catch (e) {
        throw new NamingError("Server returned empty result");
    }
    if (response.error) {
        let result;
        try {
            result = yield call(schm.validate, data, NamingApi.ErrorResult);
        } catch (errors) {
            throw new NamingError("Error response format incorrect: " + formatSchemaErrors(errors));
        }
        throw new NamingError(result.error.message);
    }
    let result;
    try {
        result = yield call(schm.validate, data, NamingApi.Result);
    } catch (errors) {
        throw new NamingError("Response format incorrect: " + formatSchemaErrors(errors));
    }
    if (result.result == null) {
        if (!notNull) {
            return null;
        } else {
            throw new NamingError("Return value is null");
        }
    }
    if (!schema) {
        return result.result;
    }
    let returnValue;
    try {
        returnValue = yield call(schm.validate, result.result, schema);
    } catch (errors) {
        throw new NamingError("Return value format incorrect: " + formatSchemaErrors(errors));
    }
    return returnValue;
}

export function* getCurrent(name, generation) {
    return yield call(callNaming,
        {method: "getCurrent", params: [name, generation], schema: NamingApi.RegisteredNameInfo});
}
