import schema from 'schm';

export const WhoAmI = schema({
    registeredName: {
        type: String,
        required: true
    },
    registeredNameGeneration: {
        type: Number,
        required: true
    }
});

export const ProfileInfo = schema({
    registeredName: {
        type: String,
        required: true
    },
    registeredNameGeneration: {
        type: Number,
        required: true
    },
    fullName: String,
    gender: String,
    operations: {
        edit: [String]
    }
});
