import schema from 'schm';

export const Result = schema({
    errorCode: {
        type: String,
        required: true
    },
    message: {
        type: String,
        default: ""
    }
});

export const WhoAmI = schema({
    registeredName: {
        type: String,
        required: true
    },
    registeredNameGeneration: {
        type: Number,
        required: true
    }
});

export const ProfileInfo = schema({
    registeredName: {
        type: String,
        required: true
    },
    registeredNameGeneration: {
        type: Number,
        required: true
    },
    fullName: String,
    gender: String,
    operations: {
        edit: [String]
    }
});

export const TokenCreated = schema({
    token: {
        type: String,
        required: true
    },
    permissions: {
        type: [String],
        default: []
    }
});
