import schema from 'schm';

export const Result = schema({
    errorCode: {
        type: String,
        required: true
    },
    message: {
        type: String,
        default: ""
    }
});

export const WhoAmI = schema({
    registeredName: String,
    registeredNameGeneration: Number
});

export const ProfileInfo = schema({
    registeredName: String,
    registeredNameGeneration: Number,
    fullName: String,
    gender: String,
    operations: {
        edit: [String]
    }
});

export const TokenCreated = schema({
    token: {
        type: String,
        required: true
    },
    permissions: {
        type: [String],
        default: []
    }
});
