import { select, call, apply } from 'redux-saga/effects';

import { NodeError } from "error";

function* callNode(location) {
    const rootApi = yield select(state => state.node.root.api);
    let response;
    try {
        response = yield call(fetch, rootApi + location);
    } catch (e) {
        throw new NodeError(e);
    }
    if (!response.ok) {
        throw new NodeError("Server returned error status");
    }
    return yield apply(response, response.json);
}

export function* getWhoAmISaga() {
    return yield call(callNode, "/whoami");
}
export function* getProfileSaga() {
    return yield call(callNode, "/profile");
}
