import { PROFILE_SET } from "profile/actions";

const initialState = {
    registeredName: null,
    registeredNameGeneration: null,
    fullName: null,
    gender: null
};

export default (state = initialState, action) => {
    switch (action.type) {
        case PROFILE_SET:
            return {
                ...state,
                ...action.payload.profile
            };
        default:
            return state;
    }
}
