import { PROFILE_LOAD, PROFILE_LOAD_FAILED, PROFILE_SET } from "profile/actions";

const initialState = {
    loading: false,
    registeredName: null,
    registeredNameGeneration: null,
    fullName: null,
    gender: null
};

export default (state = initialState, action) => {
    switch (action.type) {
        case PROFILE_LOAD:
            return {
                ...state,
                loading: true
            };

        case PROFILE_SET:
            return {
                ...state,
                ...action.payload.profile,
                loading: false
            };

        case PROFILE_LOAD_FAILED:
            return {
                ...state,
                loading: false
            };

        default:
            return state;
    }
}
