import { call, put } from 'redux-saga/effects';

import { errorThrown } from "error/actions";
import { profileLoadFailed, profileSet } from "profile/actions";
import * as Node from "node/sagas";

export function* profileLoadSaga() {
    try {
        const data = yield call(Node.getProfile);
        yield put(profileSet(data));
    } catch (e) {
        yield put(profileLoadFailed());
        yield put(errorThrown(e));
    }
}
