import { CONNECT_TO_HOME, DISCONNECT_FROM_HOME } from "state/home/actions";

const initialState = {
    cartes: []
};

export default (state = initialState, action) => {
    switch (action.type) {
        case CONNECT_TO_HOME:
            return {
                ...state,
                cartes: action.payload.cartes
            };

        case DISCONNECT_FROM_HOME:
            return {
                ...state,
                cartes: []
            };

        default:
            return state;
    }
}
