import { COMPOSE_POST, COMPOSE_POST_FAILED, COMPOSE_POST_SUCCEEDED } from "state/compose/actions";

const initialState = {
    posting: false
};

export default (state = initialState, action) => {
    switch (action.type) {
        case COMPOSE_POST:
            return {
                ...state,
                posting: true
            };

        case COMPOSE_POST_SUCCEEDED:
        case COMPOSE_POST_FAILED:
            return {
                ...state,
                posting: false
            };

        default:
            return state;
    }
}
