import LocationInfo from "state/navigation/LocationInfo";
import { isAtIndexPage, isAtProfilePage } from "state/navigation/selectors";
import profileLocation from "state/profile/location";

export default (state) => {
    let info = new LocationInfo();
    if (isAtIndexPage(state)) {
        return info;
    } else if (isAtProfilePage(state)) {
        return profileLocation(info, state);
    }
    return info;
}
