import { isAtIndexPage, isAtProfilePage } from "state/navigation/selectors";
import profileLocation from "state/profile/location";
import { atOwner } from "util/util";

export default (state, info) => {
    if (isAtIndexPage(state)) {
        return info.withTitle("Timeline" + atOwner(state));
    }
    if (isAtProfilePage(state)) {
        return profileLocation(state, info);
    }
    return info;
}
