import { isAtIndexPage, isAtProfilePage } from "state/navigation/selectors";
import { parse as profileParse, build as profileBuild } from "state/profile/location";
import { atOwner } from "util/util";

export function parse(info) {
    if (info.directories[0] === "profile") {
        return profileParse(info);
    }
    return [];
}

export function build(state, info) {
    if (isAtIndexPage(state)) {
        return info.withTitle("Timeline" + atOwner(state));
    }
    if (isAtProfilePage(state)) {
        return profileBuild(state, info);
    }
    return info;
}
