import { GO_TO_PAGE, LOCATION_SET } from "state/navigation/actions";
import { PAGE_INDEX } from "state/navigation/pages";

const initialState = {
    page: PAGE_INDEX,
    location: "",
    title: ""
};

export default (state = initialState, action) => {
    switch (action.type) {
        case GO_TO_PAGE:
            return {
                ...state,
                page: action.payload.page
            };

        case LOCATION_SET:
            return {
                ...state,
                ...action.payload
            };

        default:
            return state;
    }
}
