import { INIT_FROM_LOCATION } from "state/navigation/actions";

const initialState = {
    root: {
        location: null,
        page: null,
        api: null
    }
};

export default (state = initialState, action) => {
    switch (action.type) {
        case INIT_FROM_LOCATION:
            const location = action.payload.rootLocation;
            const page = location + "/moera";
            const api = page + "/api";
            return {
                ...state,
                root: {
                    location,
                    page,
                    api
                }
            };

        default:
            return state;
    }
}
