import moment from 'moment';

import { OWNER_SET, OWNER_VERIFIED } from "state/owner/actions";

const initialState = {
    name: null,
    generation: 0,
    latest: false,
    verified: false,
    correct: false,
    verifiedAt: 0
};

export default (state = initialState, action) => {
    switch (action.type) {
        case OWNER_SET:
            return {
                ...state,
                ...action.payload,
                verified: false,
                verifiedAt: 0
            };

        case OWNER_VERIFIED:
            if (state.name === action.payload.name) {
                return {
                    ...state,
                    ...action.payload,
                    verified: true,
                    verifiedAt: moment().unix()
                };
            }
            return state;

        default:
            return state;
    }
}
