import { GO_TO_PAGE } from "state/navigation/actions";
import { PAGE_POSTING } from "state/navigation/pages";
import { POSTING_LOAD, POSTING_LOAD_FAILED, POSTING_SET } from "state/posting/actions";

const initialState = {
    id: null,
    loading: false
};

export default (state = initialState, action) => {
    switch (action.type) {
        case GO_TO_PAGE:
            if (action.payload.page === PAGE_POSTING) {
                return {
                    ...state,
                    id: action.payload.details.id,
                    loading: false
                }
            }
            return state;

        case POSTING_LOAD:
            return {
                ...state,
                loading: true
            };

        case POSTING_LOAD_FAILED:
        case POSTING_SET:
            return {
                ...state,
                loading: false
            };

        default:
            return state;
    }
}
