import { call, put, select } from 'redux-saga/effects';

import { Node } from "api";
import { errorThrown } from "state/error/actions";
import { postingLoadFailed, postingSet } from "state/posting/actions";
import { getPostingId } from "state/posting/selectors";

export function* postingLoadSaga() {
    try {
        const id = yield select(getPostingId);
        const data = yield call(Node.getPosting, id);
        yield put(postingSet(data));
    } catch (e) {
        yield put(postingLoadFailed());
        yield put(errorThrown(e));
    }
}
