import selectn from 'selectn';

export function getPostingId(state) {
    return state.posting.id;
}

export function getPosting(state) {
    return selectn(["postings", getPostingId(state), "posting"], state);
}

export function isPostingToBeLoaded(state) {
    return !state.posting.loading && getPosting(state) == null;
}
