import { conj, trigger } from "state/trigger";
import { CONNECTED_TO_HOME, DISCONNECT_FROM_HOME } from "state/home/actions";
import { GO_TO_PAGE } from "state/navigation/actions";
import { isAtPostingPage } from "state/navigation/selectors";
import { postingLoad } from "state/posting/actions";
import { isPostingToBeLoaded } from "state/posting/selectors";

export default [
    trigger(GO_TO_PAGE, conj(isAtPostingPage, isPostingToBeLoaded), postingLoad),
    trigger([CONNECTED_TO_HOME, DISCONNECT_FROM_HOME], isAtPostingPage, postingLoad),
];
