import { conj, trigger } from "state/trigger";
import { CONNECTED_TO_HOME, DISCONNECT_FROM_HOME } from "state/home/actions";
import { GO_TO_PAGE, scrollLocation } from "state/navigation/actions";
import { isAtDetailedPostingPage } from "state/navigation/selectors";
import { POSTING_SET, postingLoad } from "state/posting/actions";
import { isPostingToBeLoaded } from "state/posting/selectors";

export default [
    trigger(GO_TO_PAGE, conj(isAtDetailedPostingPage, isPostingToBeLoaded), postingLoad),
    trigger([CONNECTED_TO_HOME, DISCONNECT_FROM_HOME], isAtDetailedPostingPage, postingLoad),
    trigger(POSTING_SET, isAtDetailedPostingPage, scrollLocation),
];
