export const POSTING_SET = "POSTING_SET";
export const postingSet = (posting) => ({
    type: POSTING_SET,
    payload: {posting}
});

export const POSTING_DELETE = "POSTING_DELETE";
export const postingDelete = (id) => ({
    type: POSTING_DELETE,
    payload: {id}
});

export const POSTING_DELETED = "POSTING_DELETED";
export const postingDeleted = (posting) => ({
    type: POSTING_DELETED,
    payload: {posting}
});

export const POSTING_DELETE_FAILED = "POSTING_DELETE_FAILED";
export const postingDeleteFailed = (id) => ({
    type: POSTING_DELETE_FAILED,
    payload: {id}
});

export const POSTING_RELOAD = "POSTING_RELOAD";
export const postingReload = (id) => ({
    type: POSTING_RELOAD,
    payload: {id}
});

export const POSTING_RELOAD_FAILED = "POSTING_RELOAD_FAILED";
export const postingReloadFailed = (id) => ({
    type: POSTING_RELOAD_FAILED,
    payload: {id}
});
