import { trigger } from "state/trigger";
import { EVENT_NODE_POSTING_UPDATED } from "api/events/actions";
import { postingReload } from "state/postings/actions";
import { isPostingCached } from "state/postings/selectors";

export default [
    trigger(
        EVENT_NODE_POSTING_UPDATED,
        (state, signal) => isPostingCached(state, signal.payload.id),
        signal => postingReload(signal.payload.id)
    )
];
