import { inv, trigger } from "state/trigger";
import { isReactionsDialogHasItems } from "state/reactionsdialog/selectors";
import {
    OPEN_REACTIONS_DIALOG,
    reactionsDialogPastReactionsLoad,
    reactionsDialogUnset
} from "state/reactionsdialog/actions";
import { CONNECTED_TO_HOME, DISCONNECT_FROM_HOME } from "state/home/actions";

export default [
    trigger(OPEN_REACTIONS_DIALOG, inv(isReactionsDialogHasItems), reactionsDialogPastReactionsLoad),
    trigger([CONNECTED_TO_HOME, DISCONNECT_FROM_HOME], true, reactionsDialogUnset)
];
