export const REGISTERED_NAME_LOAD = "REGISTERED_NAME_LOAD";
export const registeredNameLoad = () => ({
    type: REGISTERED_NAME_LOAD
});

export const REGISTERED_NAME_LOAD_FAILED = "REGISTERED_NAME_LOAD_FAILED";
export const registeredNameLoadFailed = () => ({
    type: REGISTERED_NAME_LOAD_FAILED
});

export const REGISTERED_NAME_SET = "REGISTERED_NAME_SET";
export const registeredNameSet = (registeredName) => ({
    type: REGISTERED_NAME_SET,
    payload: {registeredName}
});

export const REGISTERED_NAME_UNSET = "REGISTERED_NAME_UNSET";
export const registeredNameUnset = (registeredName) => ({
    type: REGISTERED_NAME_UNSET
});
