import {
    MNEMONIC_CLOSE,
    REGISTER_NAME,
    REGISTER_NAME_DIALOG,
    REGISTER_NAME_DIALOG_CANCEL,
    REGISTER_NAME_FAILED,
    REGISTER_NAME_SUCCEEDED,
    REGISTERED_NAME_LOAD,
    REGISTERED_NAME_LOAD_FAILED,
    REGISTERED_NAME_SET,
    REGISTERED_NAME_UNSET,
    REGISTERED_NAME_UPDATE,
    REGISTERED_NAME_UPDATE_DIALOG,
    REGISTERED_NAME_UPDATE_DIALOG_CANCEL,
    REGISTERED_NAME_UPDATE_FAILED,
    REGISTERED_NAME_UPDATE_SUCCEEDED
} from "state/registeredname/actions";

const emptyInfo = {
    name: null,
    generation: null,
    operationStatus: null,
    operationStatusUpdated: null,
    operationErrorCode: null,
    operationErrorMessage: null,
    operations: {
        manage: []
    }
};

const initialState = {
    loaded: false,
    loadingPast: false,
    ...emptyInfo,
    showingRegisterDialog: false,
    registering: false,
    mnemonicName: null,
    mnemonic: null,
    showingUpdateDialog: false,
    showingChangeName: false,
    updating: false
};

export default (state = initialState, action) => {
    switch (action.type) {
        case REGISTERED_NAME_LOAD:
            return {
                ...state,
                loadingPast: true
            };

        case REGISTERED_NAME_LOAD_FAILED:
            return {
                ...state,
                loadingPast: false
            };

        case REGISTERED_NAME_SET:
            return {
                ...state,
                ...emptyInfo,
                ...action.payload.registeredName,
                loadingPast: false,
                loaded: true
            };

        case REGISTERED_NAME_UNSET:
            return {
                ...state,
                ...emptyInfo,
                loadingPast: false,
                loaded: false
            };

        case REGISTER_NAME_DIALOG:
            return {
                ...state,
                showingRegisterDialog: true
            };

        case REGISTER_NAME_DIALOG_CANCEL:
            return {
                ...state,
                showingRegisterDialog: false
            };

        case REGISTER_NAME:
            return {
                ...state,
                registering: true
            };

        case REGISTER_NAME_SUCCEEDED:
            return {
                ...state,
                showingRegisterDialog: false,
                registering: false,
                mnemonicName: action.payload.name,
                mnemonic: action.payload.mnemonic
            };

        case REGISTER_NAME_FAILED:
            return {
                ...state,
                registering: false
            };

        case MNEMONIC_CLOSE:
            return {
                ...state,
                mnemonic: null
            };

        case REGISTERED_NAME_UPDATE_DIALOG:
            return {
                ...state,
                showingUpdateDialog: true,
                showingChangeName: action.payload.changeName
            };

        case REGISTERED_NAME_UPDATE_DIALOG_CANCEL:
            return {
                ...state,
                showingUpdateDialog: false
            };

        case REGISTERED_NAME_UPDATE:
            return {
                ...state,
                updating: true
            };

        case REGISTERED_NAME_UPDATE_SUCCEEDED:
            return {
                ...state,
                showingUpdateDialog: false,
                updating: false
            };

        case REGISTERED_NAME_UPDATE_FAILED:
            return {
                ...state,
                updating: false
            };

        default:
            return state;
    }
}
