import { call, put } from 'redux-saga/effects';

import { errorThrown } from "state/error/actions";
import {
    registeredNameLoadFailed,
    registeredNameSet, registeredNameUpdateFailed, registeredNameUpdateSucceeded,
    registerNameFailed,
    registerNameSucceeded
} from "state/registeredname/actions";
import { ownerSet } from "state/owner/actions";
import { Naming, Node } from "api";

export function* registeredNameLoadSaga() {
    try {
        const data = yield call(Node.getRegisteredName);
        yield put(registeredNameSet(data));
        if (data.name) {
            yield put(ownerSet(data.name));
        }
    } catch (e) {
        yield put(registeredNameLoadFailed());
        yield put(errorThrown(e));
    }
}

export function* registerNameSaga(action) {
    const {name, onNameTaken} = action.payload;
    try {
        const free = yield call(Naming.isFree, name);
        if (!free) {
            onNameTaken();
            yield put(registerNameFailed());
            return;
        }
        const secret = yield call(Node.registerName, name);
        yield put(registerNameSucceeded(secret.name, secret.mnemonic));
    } catch (e) {
        yield put(registerNameFailed());
        yield put(errorThrown(e));
    }
}

export function* registeredNameUpdateSaga(action) {
    const {name, mnemonic} = action.payload;
    try {
        yield call(Node.updateRegisteredName, name, mnemonic);
        yield put(registeredNameUpdateSucceeded());
    } catch (e) {
        yield put(registeredNameUpdateFailed());
        yield put(errorThrown(e));
    }
}
