import { isPermitted } from "state/node/selectors";

export function isRegisteredNameToBeLoaded(state) {
    return !state.registeredName.loaded && !state.registeredName.loading;
}

export function isRegisteredNameReady(state) {
    return state.registeredName.loaded && !state.registeredName.loading;
}

export function isRegisteredNameDefined(state) {
    return isRegisteredNameReady(state) && !!state.registeredName.name;
}

export function isRegisteredNameManageable(state) {
    return isRegisteredNameReady(state) && isPermitted("manage", state.registeredName, state);
}

export function isRegisteredNameOperationPending(state) {
    switch (state.registeredName.operationStatus) {
        case "waiting":
        case "added":
        case "started":
            return true;
        default:
            return false;
    }
}

export function isRegisteredNameOperationFinished(state) {
    return state.registeredName.operationStatus != null && !isRegisteredNameOperationPending(state);
}
