import { isPermitted } from "util/permissions";
import { getNodePermissions } from "state/node/selectors";

export function isRegisteredNameToBeLoaded(state) {
    return !state.registeredName.loaded && !state.registeredName.loading;
}

export function isRegisteredNameManageable(state) {
    isPermitted("manage", state.registeredName.operations, getNodePermissions(state), state)
}
