import { isPermitted } from "util/permissions";
import { getNodePermissions } from "state/node/selectors";

export function isRegisteredNameToBeLoaded(state) {
    return !state.registeredName.loaded && !state.registeredName.loading;
}

export function isRegisteredNameReady(state) {
    return state.registeredName.loaded && !state.registeredName.loading;
}

export function isRegisteredNameDefined(state) {
    return isRegisteredNameReady(state) && !!state.registeredName.name;
}

export function isRegisteredNameManageable(state) {
    return isRegisteredNameReady(state) && isPermitted("manage", state.registeredName.operations, getNodePermissions(state), state)
}
