import { conj, trigger } from "state/trigger";
import { GO_TO_PAGE } from "state/navigation/actions";
import { isAtSettingsPage } from "state/navigation/selectors";
import { isAtSettingsNodeTab, isSettingsNodeValuesToBeLoaded } from "state/settings/selectors";
import { settingsNodeValuesLoad } from "state/settings/actions";
import { CONNECTED_TO_HOME, DISCONNECT_FROM_HOME } from "state/home/actions";

export default [
    trigger(
        GO_TO_PAGE,
        conj(isAtSettingsPage, isAtSettingsNodeTab, isSettingsNodeValuesToBeLoaded),
        settingsNodeValuesLoad
    ),
    trigger(
        [CONNECTED_TO_HOME, DISCONNECT_FROM_HOME],
        conj(isAtSettingsPage, isAtSettingsNodeTab),
        settingsNodeValuesLoad
    ),
];
