export const TIMELINE_SLICE_LOAD = "TIMELINE_SLICE_LOAD";
export const timelineSliceLoad = () => ({
    type: TIMELINE_SLICE_LOAD
});

export const TIMELINE_SLICE_LOAD_FAILED = "TIMELINE_SLICE_LOAD_FAILED";
export const timelineSliceLoadFailed = () => ({
    type: TIMELINE_SLICE_LOAD_FAILED
});

export const TIMELINE_SLICE_SET = "TIMELINE_SLICE_SET";
export const timelineSliceSet = (postings) => ({
    type: TIMELINE_SLICE_SET,
    payload: {postings}
});
