import { TIMELINE_SLICE_LOAD, TIMELINE_SLICE_LOAD_FAILED, TIMELINE_SLICE_SET } from "state/timeline/actions";

const initialState = {
    loaded: false,
    loading: false,
    postings: []
};

export default (state = initialState, action) => {
    switch (action.type) {
        case TIMELINE_SLICE_LOAD:
            return {
                ...state,
                loading: true
            };

        case TIMELINE_SLICE_LOAD_FAILED:
            return {
                ...state,
                loading: false
            };

        case TIMELINE_SLICE_SET:
            return {
                ...state,
                postings: action.payload.postings,
                loading: false,
                loaded: true
            };

        default:
            return state;
    }
}
