import { call, put } from 'redux-saga/effects';

import { Node } from "api";
import { timelineSliceLoadFailed, timelineSliceSet } from "state/timeline/actions";
import { errorThrown } from "state/error/actions";

export function* timelineSliceLoadSaga() {
    try {
        const data = yield call(Node.getTimelineSlice, null, null, 20);
        yield put(timelineSliceSet(data.postings));
    } catch (e) {
        yield put(timelineSliceLoadFailed());
        yield put(errorThrown(e));
    }
}
