import immutable from 'object-path-immutable';

import { CONNECTED_TO_HOME, DISCONNECT_FROM_HOME } from "home/actions";

const initialState = {
};

export default (state = initialState, action) => {
    switch (action.type) {
        case CONNECTED_TO_HOME:
            return immutable.set(state, [action.payload.location, "token"], action.payload.token);

        case DISCONNECT_FROM_HOME:
            return immutable.del(state, [action.payload.location]);

        default:
            return state;
    }
}
