import React from 'react';
import { connect as connectFormik } from 'formik';

import { Button } from "ui/control";

const ComposeSubmitButton = ({disabled, update, formik}) => {
    disabled = disabled || formik.values["body"].trim().length === 0;
    const title = !update ? "POST" : "UPDATE";
    return <Button variant="primary" block type="submit" disabled={disabled}>{title}</Button>
};

export default connectFormik(ComposeSubmitButton);
