import React from 'react';
import cx from 'classnames';

export class Button extends React.PureComponent {

    componentDidMount() {
        if (this.props.autoFocus && this.domNode) {
            this.domNode.focus();
        }
    }

    render() {
        const {variant, size, block = false, invisible = false, className = "", ...props} = this.props;
        const klass = cx(
            "btn",
            `btn-${variant}`, {
                "btn-sm": size === "sm",
                "btn-lg": size === "lg",
                "btn-block": block,
                "invisible": invisible
            },
            className
        );
        return (
            <button type="button" className={klass} {...props} ref={dom => {this.domNode = dom}}>
                {props.children}
            </button>
        );
    }

}
