import React from 'react';
import cx from 'classnames';

import { Loading } from "ui/control";

export class Button extends React.PureComponent {

    componentDidMount() {
        if (this.props.autoFocus && this.domNode) {
            this.domNode.focus();
        }
    }

    render() {
        const {variant, size, block = false, invisible = false, loading = false, disabled = false, className = "",
               ...props} = this.props;
        const klass = cx(
            "btn",
            `btn-${variant}`, {
                "btn-sm": size === "sm",
                "btn-lg": size === "lg",
                "btn-block": block,
                "invisible": invisible
            },
            className
        );
        return (
            <button type="button" className={klass} disabled={loading || disabled} {...props}
                    ref={dom => {this.domNode = dom}}>
                <Loading active={loading} />
                {props.children}{loading && "â€¦"}
            </button>
        );
    }

}
